package command;

import duke.Storage;
import duke.TaskList;
import duke.Ui;
import task.Deadlines;
import task.Task;

public class DeleteCommand extends Command {
    private final int taskIndex;

    /**
     * The constructor of DeleteCommand
     * @param taskIndex the index of the task that will be deleted
     */
    public DeleteCommand(int taskIndex) {
        this.taskIndex = taskIndex;
    }

    /**
     * A method to execute DeleteCommand
     * @param taskList duke's task list
     * @param ui duke's ui
     * @param storage duke's storage
     * @return A string generated by ui
     */
    @Override
    public String execute(TaskList taskList, Ui ui, Storage storage) {
        Task deletedTask = taskList.getTask(taskIndex);
        taskList.delete(this.taskIndex);
        return ui.generateResponse("Noted. I've removed this task:\n" + deletedTask + "\n" +
                    "Now you have " + (taskList.amountOfTasks()) +" tasks in the list.\n");
    }
}
